const payload = items[0].json || {};
const errors = [];
if (!Array.isArray(payload.documents) || payload.documents.length === 0) {
  errors.push('Missing documents array');
}
if (!Array.isArray(payload.target_languages) || payload.target_languages.length === 0) {
  errors.push('Missing target_languages');
}
if (!payload.branch || !payload.commit) {
  errors.push('Branch and commit metadata are required');
}
if (payload.documents_archive && payload.documents_archive_encoding && payload.documents_archive_encoding !== 'base64') {
  errors.push(`Unsupported archive encoding: ${payload.documents_archive_encoding}`);
}
if (payload.documents_archive && payload.documents_archive_format && payload.documents_archive_format !== 'gzip-json') {
  errors.push(`Unsupported archive format: ${payload.documents_archive_format}`);
}
if (errors.length) {
  throw new Error(errors.join('; '));
}
return items;

const payload = items[0].json || {};
const archive = payload.documents_archive;
if (!archive) {
  return items;
}
const encoding = payload.documents_archive_encoding || 'base64';
if (encoding !== 'base64') {
  throw new Error(`Unsupported archive encoding: ${encoding}`);
}
const format = payload.documents_archive_format || 'gzip-json';
if (format !== 'gzip-json') {
  throw new Error(`Unsupported archive format: ${format}`);
}
const zlib = require('zlib');
let decoded;
try {
  decoded = zlib.gunzipSync(Buffer.from(archive, 'base64')).toString('utf-8');
} catch (error) {
  throw new Error(`Failed to decompress documents archive: ${error.message}`);
}
let documents;
try {
  documents = JSON.parse(decoded);
} catch (error) {
  throw new Error(`Failed to parse documents archive: ${error.message}`);
}
if (!Array.isArray(documents) || !documents.length) {
  throw new Error('Decoded archive did not contain documents.');
}
payload.documents = documents;
delete payload.documents_archive;
delete payload.documents_archive_encoding;
delete payload.documents_archive_format;
items[0].json = payload;
return items;

const payload = items[0].json;
const documents = payload.documents || [];
const targets = payload.target_languages || [];
const jobs = [];
for (const doc of documents) {
  for (const lang of targets) {
    jobs.push({
      json: {
        source_path: doc.path,
        source_language: payload.default_language,
        target_language: lang,
        content: doc.content,
        checksum: doc.checksum
      }
    });
  }
}
return jobs;

const defaultLang = items[0]?.json?.source_language || 'en';
const grouped = {};
for (const item of items) {
  const locale = item.json.target_language;
  let targetPath = item.json.source_path;
  const prefix = `docs/${defaultLang}/`;
  if (targetPath.startsWith(prefix)) {
    targetPath = targetPath.replace(prefix, `docs/${locale}/`);
  } else if (targetPath.startsWith('docs/')) {
    targetPath = targetPath.replace('docs/', `docs/${locale}/`);
  } else {
    targetPath = `${locale}/${targetPath}`;
  }
  if (!grouped[locale]) {
    grouped[locale] = [];
  }
  grouped[locale].push({
    language: locale,
    path: targetPath,
    content: item.json.translated || item.json.content
  });
}
return [{ json: { translations: Object.values(grouped).flat() } }];
